  }

  render() {


    return (



      <div style={styles}>

        <div class="tab">
          {this.tabsToShow.profile ? <button class="tablinks active" onclick={this.props.showProfile}>Profile Page</button>:''}
          {this.tabsToShow.user ? <button class="tablinks" onclick={this.props.showUserPage}>User Page</button>:''}
          {this.tabsToShow.role ? <button class="tablinks" onclick={this.props.showRolePage}>Role page</button>:''}
        </div>

        <h2>Welcome {this.user.name} {"\u2728"}</h2>
        <p>List of all users on the system</p>
         <ul>{this.state.users.map(user => <ol style={{padding:15,border:"1px solid #cccccc", width:250, textAlign:"left",marginBottom:15,marginLeft:"auto", marginRight:"auto"}}><p>Name: {user.name}</p><p>Email: {user.email}</p></ol>)}</ul>
        <button
          style={{ padding: 10, backgroundColor: "red", color: "white" }}
          onClick={this.props.logoutUser}
        >
          Logout{" "}
        </button>
      </div>
    );
  }
}
