import React from "react";
import './Home.css';
import axios from "axios";

const styles = {
  fontFamily: "sans-serif",
  textAlign: "center"
};

export default class Home extends React.Component {
  constructor(props) {
    super(props);

    this.user = JSON.parse(localStorage["appState"]).user;

    this.tabsToShow = JSON.parse(localStorage["appState"]).user.tabs_show;

    this.showPage = JSON.parse(localStorage["appState"]).showPage;
    this.state = {
      token: JSON.parse(localStorage["appState"]).user.auth_token,
      users: [],
      roles: []
    };
  }

  getUserList() {

    if(this.state.token !== '' || (typeof this.state.token !== 'undefined')) {
      axios
        .get(`http://127.0.0.1:8000/api/user/list?token=${this.state.token}`)
        .then(response => {
          return response;
        })
        .then(json => {
          if (json.data.success) {
            this.setState({ users: json.data.data });

          } else alert("Login Failed!");
        })
        .catch(error => {
          // alert(`An Error Occured! ${error}`);
        });
    }
  }

  getRoleList() {

    if(this.state.token !== '' || (typeof this.state.token !== 'undefined')) {
      axios
        .get(`http://127.0.0.1:8000/api/role/list?token=${this.state.token}`)
        .then(response => {
          return response;
        })
        .then(json => {
          if (json.data.success) {
            this.setState({ roles: json.data.data });

          } else alert("Login Failed!");
        })
        .catch(error => {
          // alert(`An Error Occured! ${error}`);
        });
    }
  }

  render() {

    this.showPage = JSON.parse(localStorage["appState"]).showPage;

    if(this.showPage === 'user'){
      this.getUserList();
    }

    if(this.showPage === 'role'){
      this.getRoleList();
    }

    return (
      <div style={styles}>

        <div class="tab">
          {this.tabsToShow.profile ? <button class="tablinks profile active" onClick={this.props.showProfile}>Profile Page</button>:''}
          {this.tabsToShow.user ? <button class="tablinks user" onClick={this.props.showUserPage}>User Page</button>:''}
          {this.tabsToShow.role ? <button class="tablinks role" onClick={this.props.showRolePage}>Role page</button>:''}
        </div>

        <div id="page-content">

          {this.showPage === 'profile' ? 
            <div id="profile-page">
              <h2>Welcome {this.user.name} {"\u2728"}</h2>
              <div class="profile-content">
                <span><b>Name : </b> {this.user.name}</span><br/>
                <span><b>Email : </b> {this.user.email}</span><br/>
                <span><b>Role : </b> {this.user.role}</span>
              </div><br/>
              <button
                style={{ padding: 10, backgroundColor: "red", color: "white" }}
                onClick={this.props.logoutUser}
              >
                Logout{" "}
              </button>
            </div>
            :''}

          {this.showPage === 'user' ? 
            <div id="user-page">

              <table style={{width:"100%"}}>
                <tr>
                  <th>NAME</th>
                  <th>EMAIL</th> 
                  <th>ROLE</th>
                  <th>ACTIONS</th>
                </tr>

                {this.state.users.map(user => 
                    <tr>
                      <td>{user.name}</td>
                      <td>{user.email}</td>
                      <td>{user.name}</td>
                      <td>{user.name}</td>
                    </tr>)
                  }

              </table>

              {this.state.users = []}
              {/* <div class="user-content">
                <span><b>Name in user : </b> {this.user.name}</span><br/>
                <span><b>Email : </b> {this.user.email}</span><br/>
                <span><b>Role : </b> {this.user.role}</span>
              </div><br/> */}
              {/* <button
                style={{ padding: 10, backgroundColor: "red", color: "white" }}
                onClick={this.props.logoutUser}
              >
                Logout{" "}
              </button> */}
            </div>:''}

          {this.showPage === 'role' ? 
            <div id="role-page">
              {/* <ul>{this.state.users.map(user => <ol style={{padding:15,border:"1px solid #cccccc", width:250, textAlign:"left",marginBottom:15,marginLeft:"auto", marginRight:"auto"}}><p>Name: {user.name}</p><p>Email: {user.email}</p></ol>)}</ul> */}
              <div class="role-content">
                <span><b>Name in role: </b> {this.user.name}</span><br/>
                <span><b>Email : </b> {this.user.email}</span><br/>
                <span><b>Role : </b> {this.user.role}</span>
              </div><br/>
              {/* <button
                style={{ padding: 10, backgroundColor: "red", color: "white" }}
                onClick={this.props.logoutUser}
              >
                Logout{" "}
              </button> */}
            </div>:''}
        </div>
      </div>
    );
  }
}
